import React from 'react';
import ReviewCard from './ReviewCard'
import {NavLink} from 'react-router-dom';

function BookReview({user, book, reviews, allUsers}){

    const filteredReviews = reviews.filter((review) => review.book_id === book.id)

    const reviewComponents = filteredReviews.map((review) => 
        <ReviewCard
        key={review.id}
        review={review.review}
        allUsers={allUsers}
        user_id={review.user_id}
        book_id={review.book_id}
        />
    )

    function clickHandler(event){
        event.preventDefault
    }

    return(
        <div>
            <div>
            {book.title}
            </div>
            <div>
            {book.author}
            </div>
            <div>
            {book.publisher}
            </div>
            <div>
            {book.genre}
            </div>
            <br/>
            <br/>
            <h2>Reviews:</h2>
            <button onClick={clickHandler}>Leave A Review!</button>
            {reviewComponents}
        </div>
    )
}

export default BookReview;
