import React, {useState, useEffect} from 'react';
import {Route, Switch, useHistory} from 'react-router-dom';
import Header from "./Header";
import Nav from "./Nav";
import Login from "./Login/Login";
import ClubSearch from "./ClubSearch/ClubSearch";
import BookSearch from "./BookSearch/BookSearch";
import MyClubs from "./MyClubs/MyClubs";
import Profile from "./Profile/Profile";


function App() {

  const [user, setUser] = useState({})
  const [books, setBooks] = useState([])
  const [clubs, setClubs] = useState([])

  useEffect(() => {
    fetch('http://localhost:3001/api/v1/books')
    .then(r => r.json())
    .then((bookArray) => {
      setBooks(bookArray)
    })
  }, [])

  useEffect(() => {
    fetch('http://localhost:3001/api/v1/clubs')
    .then(r => r.json())
    .then((clubArray) => {
      setClubs(clubArray)
    })
  }, [])

  function handleSubmit(email){
    fetch('http://localhost:3001/api/v1/users')
    .then(r => r.json())
    .then((usersArray) => {
        const userObject = usersArray.filter((user) => (user.email) === (email))
        setUser(userObject)
        console.log(userObject)
        history.push('/profile/${user.id}')
    })
}

  const history = useHistory()
  return (
    <div className="app">
      <div className="header">
        <Header />
      </div>
      <div className="nav">
        <Nav />
      </div>
      <div className="body">
        <Switch>
          <Route exact path="/">
            <Login setUser={setUser} onLoginSubmit={handleSubmit}/>
          </Route>
          <Route exact path="/clubs">
            <MyClubs />
          </Route>
          <Route exact path="/profiles">
            <Profile user={user}/>
          </Route>
          <Route exact path="/clubsearch">
            <ClubSearch clubs={clubs}/>
          </Route>
          <Route exact path="/booksearch">
            <BookSearch />
          </Route>
        </Switch>
      </div>
    </div>
  );
}

export default App;
